import './App.css';
import { Component } from 'react';
import { render } from '@testing-library/react';
import React from 'react';
import { useState } from 'react';
import Profile from './Profile/Profile';
import souheil from './souheil.jpg';
import anonymous from './anonymous.jpg'

class App extends Component{

  constructor(props) {
    super(props);
    this.state = {
      fullName : "Anonymous",
      bio : "nothing to show",
      imgSrc : anonymous,
      profession : "nothing to show",
      buttonText : "who am I?"   
    };
  }

  handleClick = () => {
    this.state.fullName==='Souheil JARRAY'? (
      this.setState({fullName:'Anonymous', buttonText:"who am I?"})
    ) : (
      this.setState({fullName:'Souheil JARRAY', buttonText:'change to Anonymous'})
    );


  }
  render() {
    return (
      <div>
        <div>{this.state.fullName}</div>
        <button onClick={this.handleClick}>{this.state.buttonText}</button>
      </div>
    )
  }


}

export default App;
