import './App.css';
import { Component } from 'react';
import { render } from '@testing-library/react';
import React from 'react';
import { useState } from 'react';
import Profile from './Profile/Profile';
import souheil from './souheil.jpg';
import anonymous from './anonymous.jpg';

class App extends Component{

  buttonSize={color:'red', textAlign:'center', fontSize:'20px', margin: '4px 20px',width:'20%'};

  constructor(props) {
    super(props);
    this.state = {
      profil:{
        fullName : "Anonymous",
        bio : "nothing to show",
        imgSrc : anonymous,
        profession : "nothing to show",
      },

      buttonText : "who am I?"   
    };
  }



  render() {
    return (
      <div>
        <Profile fullName={this.state.profil.fullName} bio={this.state.profil.bio} profession={this.state.profil.profession} >{this.state.profil.imgSrc}</Profile>
        <button onClick={this.handleClick} style={this.buttonSize}>{this.state.buttonText}</button>
        <br></br>
        <label>
        <input type="checkbox"/> enable show identity
        </label>

      </div>
    )
  }

}

export default App;
