import './App.css';
import { Component } from 'react';
import { render } from '@testing-library/react';
import souheil from './souheil.jpg'
import React from 'react';
import { useState } from 'react';

class App extends Component{
  constructor(props) {
    super(props);
    this.state = {
      fullName : "Souheil JARRAY",
      bio : "2 years of experience",
      imgSrc : souheil,
      profession : "engineer"    
    };
  }

  handleClick = () => this.setState({fullName:'Anonymous'})  

  render() {
    return (
      <div>
        <div>{this.state.fullName}</div>
        <button onClick={this.handleClick}> change to anonymous</button>
      </div>
    )
  }


}

export default App;
